import { useLayoutEffect, useState } from "react"

export default () => {
    const [windowSize, setWindowSize] = useState(<const>[window.innerWidth, window.innerHeight])

    useLayoutEffect(() => {
        const cb = () => setWindowSize([window.innerWidth, window.innerHeight])
        window.addEventListener("resize", cb)

        return () => {
            window.removeEventListener("resize", cb)
        }
    }, [])

    return windowSize
}